package com.demo;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=10;i++)
		{
			System.out.println(i);
		}
		
		System.out.println();
		
		
		int a=1;
		while(a<=4)
		{
			System.out.println(a);
			a++;
		}
		
		System.out.println();
		
		do
		{
			System.out.println(a);
			a++;
		}while(a<=0);

	}

}
